mapper.select:
		/* sample.ordersheet.ordersheetList */

		select @i as totCnt, a.*
		from
		(
			select
				@i := @i + 1 as rowNum
				, CEIL((@j := @j + 1)/#{pageSize}) as pageNum
				, imsi.*
			from(
			
						
						
						order by sample.ordersheet_id desc
						
				) imsi, (select @i := 0, @j := 0) temp		
		) a			
		where pageNum = #{pageNum}
		order by ordersheet_id desc

mapper.sql:
/* listQuery 리스트 쿼리 */ select sample.ordersheet_id , sample.sample1 , sample.sample2 , sample.sample3 , sample.sample4 , sample.sample5 , sample.sample6 , sample.sample7 , sample.sample8 , sample.sample9 , sample.sample10 , sample.del_yn , sample.in_date , sample.in_user , sample.in_ip from t_ordersheet sample where del_yn = 'N'
and (sample.sample1 like CONCAT('%', #{searchValue}, '%') or sample.sample2 like CONCAT('%', #{searchValue}, '%') or sample.sample3 like CONCAT('%', #{searchValue}, '%'))
and DATE(sample.in_date) between #{searchFromDt} and #{searchToDt}

mapper.insert:
/* sample.ordersheet.ordersheetInsert */
insert into t_ordersheet( 
	sample1
	, sample2
	, sample3
	, sample4
	, sample5
	, sample6
	, sample7
	, sample8
	, sample9
	, sample10
	, in_date
	, in_user
	, in_ip 
) values ( 
	#{sample1}
	, #{sample2}
	, #{sample3}
	, #{sample4}
	, #{sample5}
	, #{sample6}
	, #{sample7}
	, #{sample8}
	, #{sample9}
	, #{sample10}
	, NOW()
	, #{in_user}
	, #{in_ip}
	)

mapper.update:
/* sample.ordersheet.ordersheetUpdate */
update t_ordersheet 
	set
	sample1 = #{sample1}
	, sample2 = #{sample2}
	, sample3 = #{sample3}
	, sample4 = #{sample4}
	, sample5 = #{sample5}
	, sample6 = #{sample6}
	, sample7 = #{sample7}
	, sample8 = #{sample8}
	, sample9 = #{sample9}
	, sample10 = #{sample10}
	, up_date = NOW()
	, up_user = #{up_user}
	, up_ip = #{up_ip}
where ordersheet_id = #{ordersheet_id}

/* sample.ordersheet.ordersheetDelete */
update t_ordersheet 
	set
	del_yn = 'Y'
	, up_date = NOW()
	, up_user = #{up_user}
	, up_ip = #{up_ip}
where ordersheet_id = #{ordersheet_id}

mapper.select:
/* sample.autoSeq */
SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES
 WHERE TABLE_SCHEMA = #{tableSC}
 AND TABLE_NAME = #{tableNM}
